mapper.select:
select p.name productName,il.change_type,il.change_quantity,il.current_quantity,il.reason,il.reference_id,il.created_time
from inventory_logs il
join product p on il.product_id = p.id
where il.product_id in

    #{id}

    and il.change_type=#{changeType}
    and il.created_time between #{startDate} and #{endDate}
order by created_time desc
